// Format the blog post for Google Sheets
const now = new Date();
const dateStr = now.toISOString().split('T')[0];

// Extract the generated blog post
const blogPost = $input.item.json.output || "Generation failed";

// Get original article info
const sourceTitle = $('RSS Feed Read').item.json.title || "Unknown";
const sourceUrl =  $('RSS Feed Read').item.json.link  || "Unknown";

// Determine status
const status = blogPost.includes("Generation failed") ? "Failed" : "Success";
const errorMsg = status === "Failed" ? "AI generation error" : "";

return {
  date: dateStr,
  sourceTitle: sourceTitle,
  sourceUrl: sourceUrl,
  blogPost: blogPost,
  status: status,
  errorMessage: errorMsg
};

// Aggregate results for email notification
const items = $input.all();
const successCount = items.filter(item => item.json.status === "Success").length;
const failCount = items.filter(item => item.json.status === "Failed").length;
const totalCount = items.length;

return {
  successCount: successCount,
  failCount: failCount,
  totalCount: totalCount,
  overallStatus: failCount === 0 ? "success" : "partial_failure",
  timestamp: new Date().toISOString()
};

